// src/pages/FaqPage.jsx
import React, { useState } from "react";
import TopBar from "../components/TopBar";
import { ChevronDownIcon, ChevronUpIcon } from "@heroicons/react/24/outline";
import { useAuth } from "../context/AuthContext";

function FaqPage() {
  const { user } = useAuth();

  const faqs = [
    {
      question: "How do I check out an item?",
      answer:
        "Go to the User Dashboard and scan or enter the item's barcode. If the item is available, a checkout form will appear for you to enter the room, client name, and event number.",
    },
    {
      question: "What happens if I scan an item that's already checked out?",
      answer:
        "The system will automatically check it back in, provided you're the user who previously checked it out. A success message will confirm the check-in.",
    },
    {
      question: "Can I view my past check-ins and check-outs?",
      answer:
        "Yes. Go to the 'History' section on your dashboard to see a full log of all your activity, filtered by date, action, and other parameters.",
    },
    {
      question: "Why is my item not showing up when I scan the barcode?",
      answer:
        "It’s likely that the item hasn’t been added to the database yet or the barcode is incorrect. Contact an admin to verify the item exists.",
    },
    {
      question: "Who has access to add or delete items?",
      answer:
        "Only users with admin role can create, update, or delete items. Regular users can only check items in and out.",
    },
    {
      question: "What should I do if I accidentally check out the wrong item?",
      answer:
        "Immediately check it back in using the same barcode and notify your supervisor so they can verify the item history.",
    },
  ];

  const [query, setQuery] = useState("");
  const [openIndex, setOpenIndex] = useState(null);
  const [sending, setSending] = useState(false);
  const [sent, setSent] = useState(false);

  const toggle = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  const filteredFaqs = faqs.filter((faq) =>
    faq.question.toLowerCase().includes(query.toLowerCase())
  );

  const sendFaqEmail = async () => {
    setSending(true);
    try {
      await fetch("/api/email/faq-question", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question: query,
          username: user.username,
        }),
      });
      setSent(true);
    } catch (err) {
      alert("Failed to send your question. Please try again later.");
    }
    setSending(false);
  };

  return (
    <>
      <TopBar />
      <div className="bg-gray-900 text-white min-h-screen px-4 py-8">
        <h2 className="text-3xl font-bold text-asuGold mb-6 text-center">FAQ</h2>

        <div className="max-w-2xl mx-auto mb-8">
          <input
            type="text"
            placeholder="Search your question..."
            className="w-full bg-gray-800 border border-gray-700 px-4 py-3 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring focus:ring-asuGold"
            value={query}
            onChange={(e) => {
              setQuery(e.target.value);
              setSent(false);
              setOpenIndex(null);
            }}
          />
        </div>

        <div className="max-w-3xl mx-auto space-y-4">
          {filteredFaqs.length > 0 ? (
            filteredFaqs.map((faq, i) => (
              <div
                key={i}
                className="bg-gray-800 rounded-lg shadow-lg transition-all"
              >
                <button
                  onClick={() => toggle(i)}
                  className="w-full text-left px-5 py-4 flex items-center justify-between focus:outline-none"
                >
                  <h3 className="text-lg font-semibold text-asuGold">{faq.question}</h3>
                  {openIndex === i ? (
                    <ChevronUpIcon className="h-5 w-5 text-gray-300" />
                  ) : (
                    <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                  )}
                </button>
                {openIndex === i && (
                  <div className="px-5 pb-5 text-gray-300 transition-all duration-200">
                    {faq.answer}
                  </div>
                )}
              </div>
            ))
          ) : (
            <div className="text-center text-white/80 mt-10">
              <p className="mb-4">We couldn’t find anything matching your question.</p>
              {!sent ? (
                <button
                  onClick={sendFaqEmail}
                  disabled={sending}
                  className="bg-asuGold text-black font-medium px-6 py-3 rounded-full shadow hover:opacity-90 transition"
                >
                  {sending ? "Sending..." : "Still have questions? Send it to us"}
                </button>
              ) : (
                <p className="text-green-400 font-medium">Your question has been sent!</p>
              )}
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default FaqPage;
