import React from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";

function ClientOptions() {
  const navigate = useNavigate();

  const options = [
    {
      label: "Mall Reservation Check-In",
      path: "/checkin",
      color: "bg-green-600 hover:bg-green-700",
      icon: "ðŸŸ¢",
    },
    {
      label: "Mall Reservation Check-Out",
      path: "/checkout",
      color: "bg-blue-600 hover:bg-blue-700",
      icon: "ðŸ”µ",
    },
    {
      label: "Mall Reservation No-Show",
      path: "/noshow",
      color: "bg-red-600 hover:bg-red-700",
      icon: "ðŸ”´",
    },
  ];

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center px-4 py-10">
        <div className="w-full max-w-2xl bg-gradient-to-br from-gray-800 to-gray-700 rounded-2xl shadow-xl p-8 border border-gray-700">
          <h2 className="text-3xl font-bold text-center text-asuGold mb-8">
            Mall Reservation Tabling Options
          </h2>

          <div className="flex flex-col gap-5">
            {options.map((option, index) => (
              <button
                key={index}
                onClick={() => navigate(option.path)}
                className={`flex items-center justify-between text-left px-6 py-4 rounded-xl text-white font-semibold text-lg transition duration-200 ${option.color}`}
              >
                <span>{option.label}</span>
                <span className="text-2xl">{option.icon}</span>
              </button>
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default ClientOptions;
