import React, { useState, useEffect } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";
import { useAuth } from "../context/AuthContext";

function ClientNoShow() {
  const { user } = useAuth();
  const [orgList, setOrgList] = useState([]);
  const [selectedOrg, setSelectedOrg] = useState("");
  const [eventNumber, setEventNumber] = useState("");
  const [tablingSpot, setTablingSpot] = useState("");
  const [rangeStart, setRangeStart] = useState("");
  const [rangeEnd, setRangeEnd] = useState("");
  const [feedback, setFeedback] = useState("");

  useEffect(() => {
    const fetchOrganizations = async () => {
      try {
        const res = await axios.get("/api/organizations");
        setOrgList(res.data);
      } catch (err) {
        console.error("Failed to fetch organizations:", err);
      }
    };
    fetchOrganizations();
  }, []);

  const handleSubmit = async () => {
    try {
      await axios.post("/api/organizations/noshow", {
        organization: selectedOrg,
        eventNumber,
        tablingSpot,
        rangeStart,
        rangeEnd,
        username: user.username,
      });
      setFeedback("✅ No-show logged successfully.");
      setSelectedOrg("");
      setEventNumber("");
      setTablingSpot("");
      setRangeStart("");
      setRangeEnd("");
    } catch (err) {
      console.error("No-show submission failed:", err);
      setFeedback("❌ Failed to log no-show.");
    }
  };

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white p-6 flex flex-col items-center">
        <h2 className="text-3xl font-bold text-asuGold mb-6">Client No-Show</h2>

        <div className="w-full max-w-xl space-y-4">
          <input
            type="text"
            placeholder="Organization Name"
            value={organization}
            onChange={(e) => setOrganization(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />
          <input
            type="text"
            placeholder="Event #"
            value={eventNumber}
            onChange={(e) => setEventNumber(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />
          <input
            type="text"
            placeholder="Tabling Spot"
            value={tablingSpot}
            onChange={(e) => setTablingSpot(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />
          <input
            type="datetime-local"
            value={rangeStart}
            onChange={(e) => setRangeStart(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />
          <input
            type="datetime-local"
            value={rangeEnd}
            onChange={(e) => setRangeEnd(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />

          <button
            onClick={handleSubmit}
            className="w-full bg-asuGold text-black font-bold py-3 rounded hover:brightness-110"
          >
            Log No-Show
          </button>

          {feedback && <p className="text-center font-semibold text-asuGold mt-4">{feedback}</p>}
        </div>
      </div>
      <Footer />
    </>
  );

}

export default ClientNoShow;
