// TopBar.jsx
import React from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";

const TopBar = ({ currentPage }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  const getDashboardPath = () => {
    switch (user?.role) {
      case "admin":
        return "/admin-dashboard";
      case "ems":
        return "/ems-dashboard";
      case "user":
        return "/user-dashboard";
      default:
        return "/";
    }
  };

  const getRoleLabel = () => {
    if (user?.role === "admin") return "Pro Staff";
    if (user?.role === "user") return "Building Manager";
    return user?.role || "User";
  };

  return (
    <header className="sticky top-0 z-50 backdrop-blur-sm bg-gray-900/90 border-b border-gray-700 text-white">
      <div className="flex justify-between items-center px-6 py-3">
        {/* Logo + Dashboard Link */}
        <div className="flex items-center gap-4">
          <Link to={getDashboardPath()} className="hover:opacity-80 transition-opacity">
            <img
              src="https://newamericanuniversity.asu.edu/modules/composer/webspark-module-asu_footer/img/ASU-EndorsedLogo.png"
              alt="ASU Logo"
              className="h-8"
            />
          </Link>
          {currentPage && (
            <h1 className="text-lg font-semibold text-white/90">{currentPage}</h1>
          )}
        </div>

        {/* User Info + Actions */}
        <div className="flex items-center gap-4">
          {user && (
            <div className="text-right">
              <p className="text-sm font-semibold">{user.username}</p>
              <span className="text-xs bg-blue-600 px-2 py-0.5 rounded-full font-medium">
                {getRoleLabel()}
              </span>
            </div>
          )}
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-1.5 rounded text-sm font-medium"
          >
            Logout
          </button>
        </div>
      </div>
    </header>
  );
};

export default TopBar;
