// src/pages/AdminDashboard.jsx
import React from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";
import { useAuth } from "../context/AuthContext";

function AdminDashboard() {
  const navigate = useNavigate();
  const { user } = useAuth();

  const adminDashboardCards = [
    {
      title: "User Management",
      description: "Create, view, edit, or remove user accounts and assign roles.",
      path: "/admin-dashboard/users",
      icon: "ðŸ‘¥",
    //  date: "Updated Weekly",
      tag: "Accounts"
    },
    {
      title: "Item Management",
      description: "Add, edit, view or delete items. Organized by building and category.",
      path: "/admin-dashboard/items",
      icon: "ðŸ“¦",
     // date: "Updated Daily",
      tag: "Inventory"
    },
    {
      title: "Log Items",
      description: "Record the return of checked-out equipment and update inventory status.",
      path: "/scan",
      icon: "ðŸ“¦",
     // date: "Real Time",
      tag: "Check In/Out"
    },
    {
      title: "System Logs",
      description: "Review backend activity and access history logs.",
      path: "/admin-dashboard/system-logs",
      icon: "ðŸ“œ",
     // date: "Live Logs",
      tag: "Review"
    },
    {
      title: "Verification Logs",
      description: "View checklist submissions and notes from staff equipment verifications.",
      path: "/admin-dashboard/verification-logs",
      icon: "âœ…",
      tag: "Review"
    },
    {
      title: "Barcodes / QR Codes Lookup",
      description: "Look up barcodes and QR codes for inventory items.",
      path: "/admin-dashboard/barcodes",
      icon: "ðŸ”–",
    //  date: "On Demand",
      tag: "Lookup"
    },
    {
      title: "Generate Codes",
      description: "Create new barcodes or QR codes and mark them as unassigned.",
      path: "/admin-dashboard/generate-codes",
      icon: "âž•",
      date: "Manual",
      tag: "Tools"
    },

  ];  

  return (
    <>
      <TopBar />

      <div className="p-6 min-h-screen bg-gray-900 text-white">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">
          Welcome, {user?.username}!
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {adminDashboardCards.map((card, index) => (
            <div
              key={index}
              onClick={() => navigate(card.path)}
              className="relative cursor-pointer bg-gradient-to-br from-gray-800 to-gray-700 rounded-2xl p-5 shadow-xl hover:scale-[1.02] transition duration-300 border border-gray-600"
            >
              <div className="text-sm text-gray-400 mb-1">{card.date}</div>
              <h3 className="text-xl font-bold text-white mb-1">{card.title}</h3>
              <p className="text-gray-300 text-sm mb-4">{card.description}</p>

              <div className="flex justify-between items-center">
                <span className="text-sm text-white/70 bg-gray-600 rounded-full px-3 py-1 font-medium">
                  {card.tag}
                </span>
                <span className="text-2xl">{card.icon}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}

export default AdminDashboard;
