import React, { useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";
import { useAuth } from "../context/AuthContext";
import moment from "moment";

function ClientNoShow() {
  const { user } = useAuth();
  const [organization, setOrganization] = useState("");
  const [tablingSpot, setTablingSpot] = useState("");
  const [eventNumber, setEventNumber] = useState("");
  const [rangeStart, setRangeStart] = useState("");
  const [rangeEnd, setRangeEnd] = useState("");
  const [feedback, setFeedback] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/organizations/noshow", {
        organization,
        tablingSpot,
        eventNumber,
        rangeStart,
        rangeEnd,
        username: user.username,
      });
      setFeedback("No-show successfully logged.");
      setOrganization("");
      setTablingSpot("");
      setEventNumber("");
      setRangeStart("");
      setRangeEnd("");
    } catch (err) {
      console.error("Error logging no-show:", err);
      setFeedback("Failed to log no-show. Try again.");
    }
  };

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white p-6 flex flex-col items-center">
        <h2 className="text-3xl font-bold text-asuGold mb-6">Log No-Show</h2>

        <form
          onSubmit={handleSubmit}
          className="w-full max-w-xl bg-gray-800 p-6 rounded-lg shadow-lg space-y-4"
        >
          <input
            type="text"
            placeholder="Organization Name"
            value={organization}
            onChange={(e) => setOrganization(e.target.value)}
            className="w-full p-3 rounded bg-gray-700 border border-gray-500"
            required
          />

          <input
            type="text"
            placeholder="Tabling Spot #"
            value={tablingSpot}
            onChange={(e) => setTablingSpot(e.target.value)}
            className="w-full p-3 rounded bg-gray-700 border border-gray-500"
            required
          />

          <input
            type="text"
            placeholder="Event #"
            value={eventNumber}
            onChange={(e) => setEventNumber(e.target.value)}
            className="w-full p-3 rounded bg-gray-700 border border-gray-500"
            required
          />

          <label className="block text-sm font-semibold text-asuGold">Reservation Time Range</label>
          <div className="flex gap-4">
            <input
              type="datetime-local"
              value={rangeStart}
              onChange={(e) => setRangeStart(e.target.value)}
              className="w-1/2 p-3 rounded bg-gray-700 border border-gray-500"
              required
            />
            <input
              type="datetime-local"
              value={rangeEnd}
              onChange={(e) => setRangeEnd(e.target.value)}
              className="w-1/2 p-3 rounded bg-gray-700 border border-gray-500"
              required
            />
          </div>

          {feedback && (
            <p className={`text-center mt-2 font-semibold ${feedback.startsWith("No-show") ? "text-green-400" : "text-red-400"}`}>
              {feedback}
            </p>
          )}

          <button
            type="submit"
            className="w-full bg-asuGold text-black py-3 font-bold rounded hover:brightness-110 mt-4"
          >
            Submit No-Show
          </button>
        </form>
      </div>
      <Footer />
    </>
  );
}

export default ClientNoShow;
