import React, { useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";
import { useAuth } from "../context/AuthContext";
import moment from "moment";

function ClientNoShow() {
  const { user } = useAuth();
  const [organization, setOrganization] = useState("");
  const [tablingSpot, setTablingSpot] = useState("");
  const [eventNumber, setEventNumber] = useState("");
  const [rangeStart, setRangeStart] = useState("");
  const [rangeEnd, setRangeEnd] = useState("");
  const [feedback, setFeedback] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("/api/organizations/noshow", {
        organization,
        tablingSpot,
        eventNumber,
        rangeStart,
        rangeEnd,
        username: user.username,
      });
      setFeedback("No-show successfully logged.");
      setOrganization("");
      setTablingSpot("");
      setEventNumber("");
      setRangeStart("");
      setRangeEnd("");
    } catch (err) {
      console.error("Error logging no-show:", err);
      setFeedback("Failed to log no-show. Try again.");
    }
  };

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center p-6">
        <h2 className="text-3xl font-bold text-asuGold mb-6">Client No-Show</h2>

        <div className="w-full max-w-md space-y-4">
          <input
            type="text"
            placeholder="Organization Name"
            value={organization}
            onChange={(e) => setOrganization(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />

          <input
            type="text"
            placeholder="Event Number"
            value={eventNumber}
            onChange={(e) => setEventNumber(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />

          <input
            type="text"
            placeholder="Tabling Spot #"
            value={tablingSpot}
            onChange={(e) => setTablingSpot(e.target.value)}
            className="w-full p-3 rounded bg-gray-800 border border-gray-600"
          />

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block mb-1 text-sm text-white/80">Start Time</label>
              <select
                value={rangeStart}
                onChange={(e) => setRangeStart(e.target.value)}
                className="w-full p-2 rounded bg-gray-800 border border-gray-600"
              >
                {timeOptions.map((t) => (
                  <option key={t.value} value={t.value}>{t.label}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block mb-1 text-sm text-white/80">End Time</label>
              <select
                value={rangeEnd}
                onChange={(e) => setRangeEnd(e.target.value)}
                className="w-full p-2 rounded bg-gray-800 border border-gray-600"
              >
                {timeOptions.map((t) => (
                  <option key={t.value} value={t.value}>{t.label}</option>
                ))}
              </select>
            </div>
          </div>

          <button
            onClick={handleSubmit}
            className="mt-4 w-full bg-asuGold text-black font-bold py-3 rounded hover:brightness-110"
          >
            Submit No-Show
          </button>

          {feedback && <p className="mt-4 text-center font-semibold text-green-400">{feedback}</p>}
          {error && <p className="mt-4 text-center text-red-400">{error}</p>}
        </div>
      </div>
      <Footer />
    </>
  );

}

export default ClientNoShow;
