// TopBar.jsx
import React from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";

const TopBar = ({ currentPage }) => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  const getDashboardPath = () => {
    switch (user?.role) {
      case "admin":
        return "/admin-dashboard";
      case "ems":
        return "/ems-dashboard";
      case "user":
        return "/user-dashboard";
      default:
        return "/";
    }
  };

  const getRoleLabel = () => {
    if (user?.role === "admin") return "Pro Staff";
    if (user?.role === "user") return "Building Manager";
    return user?.role || "User";
  };

  const goBack = () => window.history.back();

  return (
    <header className="sticky top-0 z-50 backdrop-blur-sm bg-gray-900/95 border-b border-gray-800 text-white">
      <div className="flex justify-between items-center px-6 py-3">
        {/* Left Side */}
        <div className="flex items-center gap-4">
          <button
            onClick={goBack}
            className="hover:bg-gray-800 p-2 rounded text-xl"
            title="Go back"
          >
            ‚Üê
          </button>

          <Link to={getDashboardPath()} className="hover:opacity-80 transition-opacity">
            <img
              src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b3/Arizona_State_University_logo.svg/1024px-Arizona_State_University_logo.svg.png"
              alt="ASU Logo"
              className="h-8"
            />
          </Link>

          {currentPage && (
            <span className="text-sm text-white/80 font-medium">{currentPage}</span>
          )}
        </div>

        {/* Right Side */}
        <div className="flex items-center gap-3">
          {user && (
            <>
              <span className="text-sm font-semibold">{user.username}</span>
              <span className="text-xs bg-blue-600 text-white px-2 py-1 rounded-full font-medium">
                {getRoleLabel()}
              </span>
            </>
          )}
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 px-4 py-1.5 rounded text-sm font-medium"
          >
            Logout
          </button>
        </div>
      </div>
    </header>
  );
};

export default TopBar;
