import React from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";

function ClientOptions() {
  const navigate = useNavigate();

  const actions = [
    {
      title: "Mall Reservation Check-In",
      path: "/checkin",
      description: "Check in an organization and log their table and chair reservation when they arrive for tabling.",
      color: "from-green-700 to-green-600",
    //  icon: "ðŸŸ¢",
      tag: "Check In"
    },
    {
      title: "Mall Reservation Check-Out",
      description: "Submit an organizationsâ€™s checkout and confirm return of equipment after their reservation.",
      path: "/checkout",
      color: "from-blue-700 to-blue-600",
   //   icon: "ðŸ”µ",
      tag: "Check Out"
    },
    {
      title: "Mall Reservation No-Show",
      description: "Report an organization who failed to show up for their scheduled tabling reservation.",
      path: "/noshow",
      color: "from-red-700 to-red-600",
     // icon: "ðŸ”´",
      tag: "No Show"
    },
  ];

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white px-6 py-10">
        <h2 className="text-3xl font-bold text-center text-asuGold mb-8">
          Mall Reservation Tabling Options
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {actions.map((card, index) => (
            <div
              key={index}
              onClick={() => navigate(card.path)}
              className="relative cursor-pointer bg-gradient-to-br from-blue-800 to-red-700 rounded-2xl p-5 shadow-xl hover:scale-[1.02] transition duration-300 border border-gray-600"
            >
              <div className="text-sm text-gray-400 mb-1">{card.date}</div>
              <h3 className="text-xl font-bold text-white mb-1">{card.title}</h3>
              <p className="text-gray-300 text-sm mb-4">{card.description}</p>

              <div className="flex justify-between items-center">
                <span className="text-sm text-white/70 bg-gray-600 rounded-full px-3 py-1 font-medium">
                  {card.tag}
                </span>
                <span className="text-2xl">{card.icon}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default ClientOptions;
