import React, { useEffect, useState } from "react";
import axios from "axios";
import jsPDF from "jspdf";
import autoTable from "jspdf-autotable";
import TopBar from "../components/TopBar";
import dayjs from "dayjs";

// ✅ Utility function
function getShift(timestamp) {
  const hour = dayjs(timestamp).hour();
  if (hour >= 6 && hour < 11) return "Open";
  if (hour >= 11 && hour < 15) return "First Mid";
  if (hour >= 15 && hour < 19) return "Second Mid";
  if (hour >= 19 && hour < 23) return "Close";
  return "Off Hours";
}

function VerificationLogs() {
  const [logs, setLogs] = useState([]);
  const [filteredLogs, setFilteredLogs] = useState([]);

  const [searchText, setSearchText] = useState("");
  const [shiftFilter, setShiftFilter] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  useEffect(() => {
    axios.get("/api/inventory-checks/public")
      .then((res) => {
        setLogs(res.data);
        setFilteredLogs(res.data);
      })
      .catch((err) => console.error("Error fetching verification logs:", err));
  }, []);

  useEffect(() => {
    const lowerSearch = searchText.toLowerCase();

    const filtered = logs.filter((log) => {
      const matchesSearch =
        log.user?.toLowerCase().includes(lowerSearch) ||
        log.notes?.toLowerCase().includes(lowerSearch);

      const matchesShift = shiftFilter ? getShift(log.checkedAt) === shiftFilter : true;

      const logDate = dayjs(log.checkedAt);
      const matchesStart = startDate ? logDate.isAfter(dayjs(startDate).subtract(1, "day")) : true;
      const matchesEnd = endDate ? logDate.isBefore(dayjs(endDate).add(1, "day")) : true;

      return matchesSearch && matchesShift && matchesStart && matchesEnd;
    });

    setFilteredLogs(filtered);
  }, [searchText, shiftFilter, startDate, endDate, logs]);

  const generatePDF = (log) => {
    const doc = new jsPDF({
      orientation: "portrait",
      unit: "pt",
      format: "a4"
    });

    const maroon = "#8C1D40";
    const gold = "#F59701";
    const now = dayjs();
    const pageWidth = doc.internal.pageSize.getWidth();

    doc.setFontSize(18);
    doc.setTextColor(maroon);
    doc.text("MU/STPV Inventory Verification Log", pageWidth / 2, 40, { align: "center" });

    doc.setFontSize(11);
    doc.setTextColor("#000000");
    doc.text(`User: ${log.user}`, 50, 70);
    doc.text(`Building: ${log.building}`, 50, 90);
    doc.text(`Date: ${dayjs(log.checkedAt).format("MMM D, YYYY")}`, 50, 110);
    doc.text(`Time: ${dayjs(log.checkedAt).format("h:mm A")}`, 50, 130);
    doc.text(`Shift: ${getShift(log.checkedAt)}`, 50, 150);
    doc.text(`Confirmed: ${log.confirmed ? "Yes" : "No"}`, 50, 170);
    doc.text(`Notes: ${log.notes || "N/A"}`, 50, 190);

    const formatTable = (title, items, yStart) => {
      if (!items.length) return yStart;

      autoTable(doc, {
        startY: yStart,
        head: [[title]],
        body: items.map((barcode) => [barcode]),
        styles: {
          fontSize: 10,
          cellPadding: 4,
          halign: "center",
        },
        headStyles: {
          fillColor: gold,
          textColor: "#000",
          fontStyle: "bold"
        },
        margin: { left: 50, right: 50 }
      });

      return doc.lastAutoTable.finalY + 20;
    };

    let y = 220;
    y = formatTable("✔️ Items Present", log.presentItems, y);
    y = formatTable("❌ Items Missing", log.missingItems, y);

    doc.save(`VerificationLog_${log.user}_${dayjs(log.checkedAt).format("MMDDYYYY")}.pdf`);
  };

  return (
    <>
      <TopBar />
      <div className="p-6 bg-gray-900 text-white min-h-screen">
        <h2 className="text-3xl font-bold mb-6 text-asuGold text-center">
          Verification Logs
        </h2>

        <div className="flex flex-wrap gap-4 mb-6 items-center">
          <input
            type="text"
            placeholder="Search user or notes..."
            value={searchText}
            onChange={(e) => setSearchText(e.target.value)}
            className="bg-gray-800 text-white px-3 py-2 rounded w-64"
          />
          <select
            value={shiftFilter}
            onChange={(e) => setShiftFilter(e.target.value)}
            className="bg-gray-800 text-white px-3 py-2 rounded"
          >
            <option value="">All Shifts</option>
            <option value="Open">Open</option>
            <option value="First Mid">First Mid</option>
            <option value="Second Mid">Second Mid</option>
            <option value="Close">Close</option>
            <option value="Off Hours">Off Hours</option>
          </select>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="bg-gray-800 text-white px-3 py-2 rounded"
          />
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            className="bg-gray-800 text-white px-3 py-2 rounded"
          />
        </div>

        <div className="overflow-x-auto">
          <table className="table-auto w-full border-collapse border border-gray-700 text-sm">
            <thead className="bg-gray-800">
              <tr>
                <th className="border border-gray-700 px-3 py-2">Date</th>
                <th className="border border-gray-700 px-3 py-2">Time</th>
                <th className="border border-gray-700 px-3 py-2">User</th>
                <th className="border border-gray-700 px-3 py-2">Shift</th>
                <th className="border border-gray-700 px-3 py-2">Confirmed</th>
                <th className="border border-gray-700 px-3 py-2">Notes</th>
                <th className="border border-gray-700 px-3 py-2">Log</th>
              </tr>
            </thead>
            <tbody>
              {filteredLogs.map((log, idx) => {
                const date = dayjs(log.checkedAt).format("MM/DD/YYYY");
                const time = dayjs(log.checkedAt).format("h:mm A");
                return (
                  <tr key={idx} className="hover:bg-gray-800">
                    <td className="border border-gray-700 px-3 py-1 text-center">{date}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center">{time}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center">{log.user}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center">{getShift(log.checkedAt)}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center">{log.confirmed ? "Yes" : "No"}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center max-w-xs truncate">{log.notes || "N/A"}</td>
                    <td className="border border-gray-700 px-3 py-1 text-center">
                      <button
                        onClick={() => generatePDF(log)}
                        className="text-blue-400 underline hover:text-blue-300"
                      >
                        View Log
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    </>
  );
}

export default VerificationLogs;
