import React from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate, Link } from "react-router-dom";

const TopBar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/");
  };

  const getDashboardPath = () => {
    switch (user?.role) {
      case "admin":
        return "/admin-dashboard";
      case "ems":
        return "/ems-dashboard";
      case "user":
        return "/user-dashboard";
      default:
        return "/";
    }
  };

  const getRoleLabel = (role) => {
    switch (role) {
      case "admin":
        return "Pro Staff";
      case "user":
        return "Building Manager";
      case "ems":
        return "EMS";
      default:
        return role;
    }
  };

  const getRoleBadgeStyle = (role) => {
    switch (role) {
      case "admin":
        return "bg-red-700 text-white";
      case "user":
        return "bg-blue-600 text-white";
      case "ems":
        return "bg-green-600 text-white";
      default:
        return "bg-gray-500 text-white";
    }
  };

  return (
    <header className="w-full flex items-center justify-between px-6 py-3 bg-gray-900 border-b border-gray-700 shadow-sm">
      {/* Left Side: Logo + Dashboard Link */}
      <div className="flex items-center gap-6">
        <img
          src="https://newamericanuniversity.asu.edu/modules/composer/webspark-module-asu_footer/img/ASU-EndorsedLogo.png"
          alt="ASU Logo"
          className="h-8"
        />
        {user && (
          <Link
            to={getDashboardPath()}
            className="text-asuGold font-semibold text-lg hover:underline transition"
          >
            Dashboard
          </Link>
        )}
      </div>

      {/* Right Side: User Info */}
      <div className="flex items-center gap-4">
        {user && (
          <div className="text-sm flex flex-col text-right">
            <span className="text-white font-medium">{user.username}</span>
            <span
              className={`text-xs mt-0.5 px-2 py-[2px] rounded-full text-center font-semibold ${getRoleBadgeStyle(
                user.role
              )}`}
            >
              {getRoleLabel(user.role)}
            </span>
          </div>
        )}
        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 px-4 py-1.5 rounded text-white text-sm transition"
        >
          Logout
        </button>
      </div>
    </header>
  );
};

export default TopBar;
