import React from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";

function ClientOptions() {
  const navigate = useNavigate();

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center p-6">
        <h2 className="text-3xl font-bold text-asuGold mb-8 text-center">Client Tabling Options</h2>

        <div className="flex flex-col gap-4 w-full max-w-md">
          <button
            onClick={() => navigate("/client-checkin")}
            className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 rounded"
          >
            Client Check-In
          </button>

          <button
            onClick={() => navigate("/client-checkout")}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded"
          >
            Client Check-Out
          </button>

          <button
            onClick={() => navigate("/client-noshow")}
            className="w-full bg-red-600 hover:bg-red-700 text-white font-semibold py-3 rounded"
          >
            Client No-Show
          </button>
        </div>
      </div>
      <Footer />
    </>
  );
}

export default ClientOptions;
