// frontend/src/pages/ShiftChecklist.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";
import { useAuth } from "../context/AuthContext";

function ShiftChecklist() {
  const { user } = useAuth();
  const [building, setBuilding] = useState("");
  const [items, setItems] = useState([]);
  const [checkedItems, setCheckedItems] = useState([]);
  const [submitted, setSubmitted] = useState(false);

  useEffect(() => {
    if (building) {
      axios
        .get(`/api/items?building=${encodeURIComponent(building)}&status=Available`)
        .then((res) => setItems(res.data))
        .catch((err) => console.error("Error fetching items:", err));
    }
  }, [building]);

  const handleToggle = (barcode) => {
    setCheckedItems((prev) =>
      prev.includes(barcode) ? prev.filter((b) => b !== barcode) : [...prev, barcode]
    );
  };

  const handleSubmit = async () => {
    const missingItems = items.map((item) => item.barcode).filter((b) => !checkedItems.includes(b));

    const payload = {
      user: user.username,
      building,
      time: new Date().toISOString(),
      confirmed: true,
      presentItems: checkedItems,
      missingItems,
    };

    try {
      await axios.post("/api/inventory-checks", payload, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      await axios.post("/api/notifications/inventory-check", payload, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });

      setSubmitted(true);
    } catch (err) {
      console.error("Failed to submit checklist:", err);
    }
  };

  return (
    <>
      <TopBar />
      <div className="p-6 min-h-screen bg-gray-900 text-white">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">Equipment Check</h2>

        {!building ? (
          <div className="max-w-md mx-auto mb-10">
            <label className="block mb-2 text-lg">Select Building</label>
            <select
              className="w-full p-2 bg-gray-700 border border-gray-600 rounded text-white"
              value={building}
              onChange={(e) => setBuilding(e.target.value)}
            >
              <option value="">-- Choose --</option>
              <option value="Memorial Union">Memorial Union</option>
              <option value="Student Pavilion">Student Pavilion</option>
            </select>
          </div>
        ) : (
          <>
            <div className="mb-4 text-sm text-gray-400">{items.length} items available</div>
            <div className="space-y-2">
              {items.map((item) => (
                <div
                  key={item.barcode}
                  onClick={() => handleToggle(item.barcode)}
                  className="flex items-center bg-gray-800 rounded px-4 py-2 shadow cursor-pointer"
                >
                  <input
                    type="checkbox"
                    checked={checkedItems.includes(item.barcode)}
                    onChange={() => handleToggle(item.barcode)}
                    className="mr-3 pointer-events-none"
                  />
                  <span>{item.name} – #{item.barcode}</span>
                </div>
              ))}
            </div>

            <div className="mt-6 text-center text-sm text-white/60">
              I confirm that the checked items are present in the closet.
            </div>

            <div className="text-center mt-4">
              <button
                onClick={handleSubmit}
                className="bg-green-600 hover:bg-green-700 px-6 py-2 rounded text-white font-semibold"
              >
                Submit Checklist
              </button>
            </div>

            {submitted && (
              <p className="text-center text-green-400 mt-4 font-medium">Checklist submitted!</p>
            )}
          </>
        )}
      </div>
    </>
  );
}

export default ShiftChecklist;
