// src/pages/ItemLookup.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";

function Lookup() {
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("");

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const res = await axios.get("/api/items");
        setItems(res.data || []);
      } catch (err) {
        console.error("Failed to fetch items", err);
      }
    };
    fetchItems();
  }, []);

  const filtered = items.filter((item) => {
    const matchesSearch = item.name.toLowerCase().includes(search.toLowerCase());
    const currentStatus = getStatus(item)?.status;
    const matchesStatus = statusFilter ? currentStatus === statusFilter : true;
    return matchesSearch && matchesStatus;
  });

  const getStatus = (item) => {
    const latestIssue = item.logs?.slice().reverse().find(log => log.action === "report_issue");
    if (item.archive) return { status: "Archived", color: "bg-gray-500 text-gray-300", icon: "üì¶" };
    if (item.maintenance) return { status: "Maintenance", color: "bg-orange-600 text-orange-200", icon: "üõ†Ô∏è" };
    if (item.isBroken) return { status: "Broken", color: "bg-red-600 text-red-200", icon: "üö®" };
    if (item.isMissing) return { status: "Missing", color: "bg-yellow-600 text-yellow-100", icon: "‚ùó" };
    if (latestIssue) {
      const note = latestIssue.notes?.toLowerCase() || "";
      return {
        status: "Reported",
        color: note.includes("missing") ? "bg-yellow-600 text-yellow-100" : note.includes("broken") ? "bg-red-600 text-red-200" : "bg-blue-600 text-blue-200",
        icon: "üö®"
      };
    }
    if (item.isAvailable) return { status: "Available", color: "bg-gray-800 text-green-400", icon: "‚úÖ" };
    return null;
  };

  return (
    <>
      <TopBar />
      <div className="p-6 min-h-screen bg-gray-900 text-white">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">Item Lookup</h2>

        <div className="flex flex-wrap gap-4 mb-6 justify-center">
          <input
            type="text"
            placeholder="Search by name..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="bg-gray-800 text-white px-3 py-1 rounded"
          />
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="bg-gray-800 text-white px-3 py-1 rounded"
          >
            <option value="">All Statuses</option>
            <option value="Available">Available</option>
            <option value="Broken">Broken</option>
            <option value="Missing">Missing</option>
            <option value="Reported">Reported</option>
            <option value="Maintenance">Maintenance</option>
            <option value="Archived">Archived</option>
          </select>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtered.map((item) => {
            const statusObj = getStatus(item);
            return (
              <div
                key={item._id}
                className={`${statusObj?.color || "bg-gray-800 text-white"} rounded-xl p-5 shadow-lg hover:shadow-xl transition duration-300 transform hover:scale-[1.02]`}
              >
                <div className="flex justify-between items-center mb-2">
                  <h3 className="text-lg font-bold text-white">{item.name}</h3>
                  {statusObj && (
                    <span className="text-sm font-semibold">
                      {statusObj.icon} {statusObj.status}
                    </span>
                  )}
                </div>
                <p className="text-sm text-white/80">
                  Barcode: <span className="font-mono">{item.barcode}</span>
                </p>
                <p className="text-xs text-white/60 mt-1 italic">
                  {item.building ? `Located in ${item.building}` : "No building info"}
                </p>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Lookup;
