// src/pages/ItemLookup.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";
import TopBar from "../components/TopBar";
import dayjs from "dayjs";

function Lookup() {
  const [items, setItems] = useState([]);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("Checked Out"); // default
  const [buildingFilter, setBuildingFilter] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("");
  const [selectedItem, setSelectedItem] = useState(null);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const res = await axios.get("/api/items");
        setItems(res.data || []);
      } catch (err) {
        console.error("Failed to fetch items", err);
      }
    };
    fetchItems();
  }, []);

  const getStatus = (item) => {
    const latestIssue = item.logs?.slice().reverse().find(log => log.action === "report_issue");
    if (!item.isAvailable) return { status: "Checked Out", color: "bg-red-600 text-red-200", icon: "❌" };
    if (item.archive) return { status: "Archived", color: "bg-gray-500 text-gray-300", icon: "📦" };
    if (item.maintenance) return { status: "Maintenance", color: "bg-orange-600 text-orange-200", icon: "🛠️" };
    if (item.isBroken) return { status: "Broken", color: "bg-red-600 text-red-200", icon: "🚨" };
    if (item.isMissing) return { status: "Missing", color: "bg-yellow-600 text-yellow-100", icon: "❗" };
    if (latestIssue) {
      const note = latestIssue.notes?.toLowerCase() || "";
      return {
        status: "Reported",
        color: note.includes("missing") ? "bg-yellow-600 text-yellow-100" : note.includes("broken") ? "bg-red-600 text-red-200" : "bg-blue-600 text-blue-200",
        icon: "🚨"
      };
    }
    return { status: "Available", color: "bg-gray-800 text-green-400", icon: "✅" };
  };

  const filtered = items.filter((item) => {
    const matchesSearch =
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.barcode.toLowerCase().includes(search.toLowerCase());
    const currentStatus = getStatus(item)?.status;
    const matchesStatus = statusFilter ? currentStatus === statusFilter : true;
    const matchesBuilding = buildingFilter ? item.building === buildingFilter : true;
    const matchesCategory = categoryFilter ? item.category === categoryFilter : true;
    return matchesSearch && matchesStatus && matchesBuilding && matchesCategory;
  });

  return (
    <>
      <TopBar />
      <div className="p-6 min-h-screen bg-gray-900 text-white">
        <h2 className="text-3xl font-bold mb-6 text-center text-asuGold">Item Lookup</h2>

        <div className="flex flex-wrap gap-4 mb-6 justify-center">
          <input
            type="text"
            placeholder="Search by name or barcode..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="bg-gray-800 text-white px-3 py-1 rounded"
          />
          <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value)} className="bg-gray-800 text-white px-3 py-1 rounded">
            <option value="">All Statuses</option>
            <option value="Checked Out">Checked Out</option>
            <option value="Available">Available</option>
            <option value="Broken">Broken</option>
            <option value="Missing">Missing</option>
            <option value="Reported">Reported</option>
            <option value="Maintenance">Maintenance</option>
            <option value="Archived">Archived</option>
          </select>
          <select value={buildingFilter} onChange={(e) => setBuildingFilter(e.target.value)} className="bg-gray-800 text-white px-3 py-1 rounded">
            <option value="">All Buildings</option>
            <option value="Memorial Union">Memorial Union</option>
            <option value="Student Pavilion">Student Pavilion</option>
          </select>
          <select value={categoryFilter} onChange={(e) => setCategoryFilter(e.target.value)} className="bg-gray-800 text-white px-3 py-1 rounded">
            <option value="">All Types</option>
            <option value="Mic">Mic</option>
            <option value="Lavaliers">Lavaliers</option>
            <option value="Tech Bag">Tech Bag</option>
            <option value="HDMI Cable">HDMI Cable</option>
            <option value="Clicker">Clicker</option>
            <option value="Type C Adapters">Type C Adapters</option>
            <option value="Mall Table">Mall Table</option>
            <option value="Mall Chair">Mall Chair</option>
            <option value="Easels">Easels</option>
          </select>
        </div>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {filtered.map((item) => {
            const statusObj = getStatus(item);
            return (
              <div
                key={item._id}
                className={`${statusObj?.color || "bg-gray-800 text-white"} rounded-xl p-5 shadow-lg hover:shadow-xl transition duration-300 transform hover:scale-[1.02] cursor-pointer`}
                onClick={() => setSelectedItem(item)}
              >
                <div className="flex justify-between items-center mb-2">
                  <h3 className="text-lg font-bold text-white">{item.name}</h3>
                  <span className="text-sm font-semibold">{statusObj.icon} {statusObj.status}</span>
                </div>
                <p className="text-sm text-white/80">Barcode: <span className="font-mono">{item.barcode}</span></p>
                <p className="text-xs text-white/60 mt-1 italic">{item.building ? `Located in ${item.building}` : "No building info"}</p>
              </div>
            );
          })}
        </div>

        {selectedItem && (
          <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50">
            <div className="bg-gray-800 p-6 rounded-xl w-full max-w-md">
              <h3 className="text-xl font-semibold mb-4">Checked Out Info</h3>
              <p><strong>Item:</strong> {selectedItem.name}</p>
              <p><strong>Barcode:</strong> {selectedItem.barcode}</p>
              <p><strong>Checked Out By:</strong> {selectedItem.checkedOutBy || "-"}</p>
              <p><strong>Room:</strong> {selectedItem.room || "-"}</p>
              <p><strong>Client:</strong> {selectedItem.clientName || "-"}</p>
              <p><strong>Time:</strong> {dayjs(selectedItem.logs?.slice().reverse().find(log => log.action === "check_out")?.timestamp).format("MMM D, YYYY h:mm A") || "-"}</p>
              <div className="flex justify-end mt-4">
                <button onClick={() => setSelectedItem(null)} className="bg-gray-600 px-4 py-2 rounded hover:bg-gray-700">Close</button>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

export default Lookup;
