import React, { useState, useEffect } from "react";
import TopBar from "../components/TopBar";
import { useAuth } from "../context/AuthContext";
import { PaperAirplaneIcon } from "@heroicons/react/24/outline";

function FaqPage() {
  const { user } = useAuth();
  const [query, setQuery] = useState("");
  const [filteredFaqs, setFilteredFaqs] = useState([]);
  const [openIndex, setOpenIndex] = useState(null);
  const [notFound, setNotFound] = useState(false);
  const [sent, setSent] = useState(false);
  const [sending, setSending] = useState(false);

  const faqs = [
    {
      question: "How do I check out an item?",
      answer: "Go to the User Dashboard and scan or enter the item's barcode. If the item is available, a checkout form will appear for you to enter the room, client name, and event number.",
      pdf: "https://drive.google.com/example-checkout-guide"
    },
    {
      question: "What happens if I scan an item that's already checked out?",
      answer: "The system will automatically check it back in, provided you're the user who previously checked it out.",
      pdf: "https://drive.google.com/example-checkin-guide"
    },
    {
      question: "Can I view my past check-ins and check-outs?",
      answer: "Yes. Go to the 'History' section on your dashboard to see a full log of all your activity.",
      pdf: "https://drive.google.com/example-history-guide"
    },
    {
      question: "Why is my item not showing up when I scan the barcode?",
      answer: "Itâ€™s likely that the item hasnâ€™t been added to the database yet or the barcode is incorrect. Contact an admin to verify the item exists.",
      pdf: "https://drive.google.com/example-troubleshooting-guide"
    },
    {
      question: "Who has access to add or delete items?",
      answer: "Only users with the admin role can create, update, or delete items. Regular users can only check items in and out.",
      pdf: "https://drive.google.com/example-admin-guide"
    },
    {
      question: "What should I do if I accidentally check out the wrong item?",
      answer: "Immediately check it back in and notify your supervisor to verify the item history.",
      pdf: "https://drive.google.com/example-error-fix-guide"
    }
  ];

  useEffect(() => {
    const matchedFaqs = faqs.filter((faq) =>
      faq.question.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredFaqs(matchedFaqs);
    setNotFound(query.length > 2 && matchedFaqs.length === 0);
  }, [query]);

  const sendFaqEmail = async () => {
    setSending(true);
    try {
      await fetch("/api/email/faq-question", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          question: query,
          username: user.username,
        }),
      });
      setSent(true);
    } catch (err) {
      alert("Failed to send your question. Please try again later.");
    }
    setSending(false);
  };

  return (
    <>
      <TopBar />
      <div className="bg-gray-900 text-white min-h-screen px-6 py-12">
        <h2 className="text-4xl font-bold text-center text-asuGold mb-8">FAQ</h2>

        <div className="max-w-2xl mx-auto">
          <input
            type="text"
            placeholder="Search your question..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            className="w-full bg-gray-800 border border-gray-700 rounded-lg px-4 py-2 text-white placeholder:text-gray-400 mb-6 focus:outline-none"
          />

          {notFound && (
            <div className="text-center text-sm text-white/80 mb-6">
              <p className="mb-2">We couldnâ€™t find anything matching your question.</p>
              {sent ? (
                <p className="text-green-400 font-medium">âœ… Sent! We'll follow up with you shortly.</p>
              ) : (
                <button
                  onClick={sendFaqEmail}
                  disabled={sending}
                  className={`bg-asuGold text-black font-semibold px-5 py-2 rounded-full transition ${
                    sending ? "opacity-60 cursor-not-allowed" : "hover:opacity-90"
                  }`}
                >
                  {sending ? "Sending..." : "Still have questions? Send it to us"}
                </button>
              )}
            </div>
          )}

          <div className="space-y-3">
            {filteredFaqs.map((faq, i) => (
              <div
                key={i}
                className="bg-gray-800 rounded-lg shadow border border-gray-700"
              >
                <button
                  onClick={() => setOpenIndex(openIndex === i ? null : i)}
                  className="w-full flex justify-between items-center px-5 py-4 focus:outline-none"
                >
                  <span className="text-asuGold font-medium text-left text-[16px]">
                    {faq.question}
                  </span>
                  <PaperAirplaneIcon
                    className={`h-5 w-5 transition-transform duration-200 ${openIndex === i ? "rotate-180 text-asuGold" : "text-white/60"}`}
                  />
                </button>

                {openIndex === i && (
                  <div className="px-5 pb-5 text-sm text-gray-300">
                    <p className="mb-2">{faq.answer}</p>
                    {faq.pdf && (
                      <a
                        href={faq.pdf}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-asuGold underline text-sm hover:text-yellow-400"
                      >
                        ðŸ“„ View Guide (PDF)
                      </a>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
}

export default FaqPage;
