import React, { useState } from "react";
import TopBar from "../components/TopBar";
import { ChevronDownIcon, ChevronUpIcon } from "@heroicons/react/24/outline";
import { useAuth } from "../context/AuthContext";

function FaqPage() {
  const { user } = useAuth();

  const faqs = [
    {
      category: "What is?",
      question: "How do I check out an item?",
      answer:
        "Go to the User Dashboard and scan or enter the item's barcode. If the item is available, a checkout form will appear for you to enter the room, client name, and event number.",
    },
    {
      category: "What is?",
      question: "Can I view my past check-ins and check-outs?",
      answer:
        "Yes. Go to the 'History' section on your dashboard to see a full log of all your activity, filtered by date, action, and other parameters.",
    },
    {
      category: "How to?",
      question: "What happens if I scan an item that's already checked out?",
      answer:
        "The system will automatically check it back in, provided you're the user who previously checked it out. A success message will confirm the check-in.",
    },
    {
      category: "Do I need?",
      question: "Who has access to add or delete items?",
      answer:
        "Only users with admin role can create, update, or delete items. Regular users can only check items in and out.",
    },
    {
      category: "How to?",
      question: "What should I do if I accidentally check out the wrong item?",
      answer:
        "Immediately check it back in using the same barcode and notify your supervisor so they can verify the item history.",
    },
    {
      category: "Do I need?",
      question: "Why is my item not showing up when I scan the barcode?",
      answer:
        "It’s likely that the item hasn’t been added to the database yet or the barcode is incorrect. Contact an admin to verify the item exists.",
    },
  ];

  const [openIndex, setOpenIndex] = useState(null);
  const toggle = (index) => setOpenIndex(openIndex === index ? null : index);

  const grouped = faqs.reduce((acc, cur) => {
    acc[cur.category] = acc[cur.category] || [];
    acc[cur.category].push(cur);
    return acc;
  }, {});

  const mailto = `mailto:vinny.24@asu.edu?subject=Question about the inventory logging system&body=Hello my name is ${user.username}, I have a question about the logging system and they are as follows:%0A`;

  return (
    <>
      <TopBar />
      <div className="bg-gray-900 text-white min-h-screen px-6 py-8">
        <h2 className="text-4xl font-bold text-asuGold mb-10 text-center">FAQ</h2>

        <div className="max-w-2xl mx-auto mb-10">
          <input
            type="text"
            placeholder="Hi, how can we help you?"
            className="w-full bg-gray-800 text-white px-4 py-3 rounded-xl shadow-sm placeholder:text-white/60 focus:outline-none border border-gray-700 focus:border-asuGold transition"
          />
        </div>

        <div className="max-w-4xl mx-auto space-y-10">
          {Object.entries(grouped).map(([category, items], catIndex) => (
            <div key={catIndex}>
              <h3 className="text-2xl font-semibold mb-4 text-white/90">
                {category}
              </h3>
              <div className="space-y-3">
                {items.map((faq, i) => {
                  const index = `${catIndex}-${i}`;
                  return (
                    <div
                      key={index}
                      className="bg-gray-800 border border-gray-700 rounded-lg transition-all"
                    >
                      <button
                        onClick={() => toggle(index)}
                        className="w-full flex justify-between items-center px-6 py-4 focus:outline-none"
                      >
                        <span className="text-lg text-white font-medium">{faq.question}</span>
                        {openIndex === index ? (
                          <ChevronUpIcon className="h-5 w-5 text-gray-300" />
                        ) : (
                          <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                        )}
                      </button>
                      {openIndex === index && (
                        <div className="px-6 pb-5 text-sm text-gray-300">
                          {faq.answer}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>

        <div className="text-center mt-12">
          <a
            href={mailto}
            target="_blank"
            rel="noopener noreferrer"
            className="inline-block bg-asuGold text-black font-medium px-6 py-3 rounded-full shadow hover:opacity-90 transition"
          >
            Got more questions?
          </a>
        </div>
      </div>
    </>
  );
}

export default FaqPage;
