import React from "react";
import { useNavigate } from "react-router-dom";
import TopBar from "../components/TopBar";
import Footer from "../components/Footer";

function ClientOptions() {
  const navigate = useNavigate();

  const actions = [
    {
      label: "Mall Reservation Check-In",
      path: "/checkin",
      color: "from-green-700 to-green-600",
      icon: "ðŸŸ¢",
      tag: "Check In"
    },
    {
      label: "Mall Reservation Check-Out",
      path: "/checkout",
      color: "from-blue-700 to-blue-600",
      icon: "ðŸ”µ",
      tag: "Check Out"
    },
    {
      label: "Mall Reservation No-Show",
      path: "/noshow",
      color: "from-red-700 to-red-600",
      icon: "ðŸ”´",
      tag: "No Show"
    },
  ];

  return (
    <>
      <TopBar />
      <div className="min-h-screen bg-gray-900 text-white px-6 py-10">
        <h2 className="text-3xl font-bold text-center text-asuGold mb-8">
          Mall Reservation Tabling Options
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 max-w-5xl mx-auto">
          {actions.map((action, index) => (
            <div
              key={index}
              onClick={() => navigate(action.path)}
              className={`cursor-pointer rounded-2xl p-5 shadow-xl hover:scale-[1.02] transition duration-300 bg-gradient-to-br ${action.color} border border-gray-600`}
            >
              <h3 className="text-xl font-bold text-white mb-1">{action.label}</h3>
              <p className="text-sm text-gray-200 mb-4">Log tabling activity for client reservations.</p>

              <div className="flex justify-between items-center">
                <span className="text-sm bg-gray-800/70 text-white px-3 py-1 rounded-full font-medium">
                  {action.tag}
                </span>
                <span className="text-2xl">{action.icon}</span>
              </div>
            </div>
          ))}
        </div>
      </div>
      <Footer />
    </>
  );
}

export default ClientOptions;
